import React from 'react'
import {Link} from "react-router-dom";
import classes from './SideMenu.module.css'


class SideMenu extends React.Component {

  backDrop = () => {
    return (
      <div
        className={classes.BackDrop}
        onClick={this.props.onClick}
      >

      </div>
    )
  }

  render() {
    const cls = [ classes.SideMenu ]

    if (!this.props.isMenuOpened) {
      cls.push(classes.close)
    }

    return (
        <div>
          <nav className={cls.join(' ')}>
            <small>Menu</small>
            <ul>

                <Link to={"/"} className={classes.MenuItem}>Log in</Link>

                <Link to={"/register"} className={classes.MenuItem}>Register</Link>

                <Link to={"/play"} className={classes.MenuItem}>Play</Link>

                <Link to={"/levels"} className={classes.MenuItem}>Select level</Link>


            </ul>

          </nav>

          {this.props.isMenuOpened ? this.backDrop() : null}
        </div>
    )
  }
}

export default SideMenu

