import React from 'react'
import classes from './Button.module.css'

const Button = props => {
  const cls = [
    classes.Button,
    classes[props.type]
    ]
  return(
      <button
        onClick={props.onClick}
        className={cls.join(' ')}
        disabled={props.disabled}
      >
        {props.caption}
      </button>
  )
}
export default Button
