import React from 'react'
import App from "../../App";

class Sections extends React.Component{

  state = {
    sections:null
  }


  componentDidMount() {
    if(!this.state.section) {
      this.onGetSections()
    }
  }
  async onGetSections () {
    const res = await App.serverController.getSections()
    console.log('getSections', res)
    this.state.section = res.sections
  }

  renderSections = () => {
    console.log(renderSections)
    return this.state.sections.map(index => {
      return <li>{this.state.sections[index].name}</li>
    })
  }

  render() {

    return (
      <div>
        <span>Select section:</span>
        <ul>
          {this.sections ? this.renderSections() : null}
        </ul>
      </div>
    )
  }
}
export default Sections
