import React from 'react'
import classes from './LogIn.module.css'
import Input from "../ui/input/Input";
import Button from "../ui/button/Button";
import App from "../../App";

class Login extends React.Component{

  state = {
    loginString: '',
    passwordString: ''
  }

  async onLogin (e) {
    e.preventDefault();
    const res = await App.serverController.login({"login" : this.state.loginString, "password" : this.state.passwordString})
    console.log('onLogin', res)
