import React from 'react'
import classes from './MenuToggle.module.css'

const MenuToggle = props => {

  const cls = [
    classes.MenuToggle,
    'fa'
  ]

  if(props.isOpen) {
    cls.push('fas-times')
    cls.push(classes.open)
  } else {
    cls.push('fas-bars')

  }

  return (
    <is
      className={cls.join(' ')}
      onClick={props.onToggle}
    >


    </is>
  )
}

export  default MenuToggle
