import React from 'react'

class Query extends React.Component {

  renderOptions () {
    return this.props.options.map((option, index) => {
      return (
        <li>
          <button>{option.text}</button>
        </li>
      )
    })
  }


  render() {

    return (
      <div>
        <h1>Hello, how ... you?</h1>
        <ul>
          {this.renderOptions()}
        </ul>
        <small>Вопрос 1 из 20</small>
      </div>
    )
  }
}

export default Query
