import React from 'react'
import App from "../../App";

class Sections extends React.Component{

  state = {
    sections:null
  }


  componentDidMount() {
    if(!this.state.sections) {
      this.onGetSections()
    }
  }
  async onGetSections () {
    const res = await App.serverController.getSections()
    console.log('getSections', res)
    this.state.sections = res.sections
  }

  renderSections = () => {
    debugger

    console.log('renderSections')
    return this.state.sections.map(index => {
      return (<li>{this.state.sections[index].name}</li>)
    })
  }

  render() {

    return (
      <div>

          {this.state.sections !== null

            ? <ul>{ () => this.renderSections() }</ul>
            : null
          }

      </div>
    )
  }
}
export default Sections
