import React from 'react'
import classes from './LogIn.module.css'
import Input from "../ui/input/Input";
import Button from "../ui/button/Button";
import App from "../../App";
import { Redirect } from "react-router-dom";


class Register extends React.Component{

  state = {
    loginString: '',
    passwordString: ''
  }

  async onRegister (e) {
    e.preventDefault();
    const res = await App.serverController.register({"email" : this.state.loginString, "password" : this.state.passwordString})
    console.log('onRegister', res)
    /*if(res.state === 'success') {
      this.setState({
        redirect: true
      })
    }*/
  }

  onLoginInput = (e) => {
    const loginString = e.target.value;
    console.log(loginString)
    this.setState({
      loginString
    })
  }

  onPasswordInput = (e) => {
    const passwordString = e.target.value;
    this.setState({
      passwordString
    })
  }

  renderRedirect = () => {
    if (this.state.redirect) {
      return <Redirect to='/' />
    }
  }

  render() {

    return (
      <div className={classes.LogIn}>
        {this.renderRedirect()}

        <div>
          <h1>Register new user</h1>

          <form className={classes.Form}>
            <Input label={'Enter email'}
                   onChange={this.onLoginInput}
            />
            <Input label={'Enter password'}
                   onChange={this.onPasswordInput}
            />

            <Button
              type="success"
              caption='Register'
              onClick={this.onRegister.bind(this)}
            >
            </Button>
          </form>
        </div>
      </div>
    )
  }
}
export default Register
