import React from 'react'
import classes from './LogIn.module.css'
import Input from "../ui/input/Input";
import Button from "../ui/button/Button";
import ServerController from "../../server/ServerController";

class Login extends React.Component{

  state = {
    loginString: '',
    passwordString: ''
  }

  componentDidMount() {
    this.serverController = new ServerController()
    this.serverController.register({"login" : "artem", "password" : "Ajtdmw12"})
  }

  onLogin = (e) => {
    e.preventDefault();
    const login = e.target.value
    this.serverController.login({"login" : this.state.loginString, "password" : this.state.passwordString})
  }

  onLoginInput = (e) => {
    const loginString = e.target.value;
    console.log(loginString)
    this.setState({
      loginString
    })
  }

  onPasswordInput = (e) => {
    const passwordString = e.target.value;
    this.setState({
      passwordString
    })
  }

  render() {

    return (
      <div className={classes.LogIn}>
        <div>
          <h1>User login</h1>

          <form className={classes.Form}>
            <Input label={'Enter email'}
                   onChange={this.onLoginInput}
            />
            <Input label={'Enter password'}
                   onChange={this.onPasswordInput}
            />

            <Button
              type="success"
              caption='Log in'
              onClick={this.onLogin}
            >
            </Button>
          </form>
        </div>
      </div>
    )
  }
}
export default Login
