import axios from 'axios'

export default class ServerController {
  constructor () {
    this.url = `http://localhost:8800`
    axios.defaults.headers.post['Content-Type'] ='application/json;charset=utf-8';
    axios.defaults.headers.post['Access-Control-Allow-Origin'] = '*';
  }

  async getInit() {
    const response = await fetch('http://localhost:8800/', JSON.stringify({"action":"init","user_id":Math.random() * 10}))
    console.log(response)
    return response
  }

  async post(data) {
    try {
      // Create request to api service
      const req = await fetch(`http://localhost:8800/`, {
        method: 'POST',
        headers: { 'Content-Type':'application/json',
          'Access-Control-Allow-Origin':'*'},

        // format the data
        body: JSON.stringify(data),
      });

      const res = await req.json();

      // Log success message
      console.log(res);
    }catch(err) {
      console.error(`ERROR: err`);
    }
  }

  getSpin() {
    axios.post('http://localhost:8800/', {body: 'spin'}).then(response => {
      this.serverHandshake = response.data
    })
      .catch((error) => {
        console.log(error);
      });
  }

}
